create table alternate_name (board_game_id bigint, id bigint not null auto_increment, name varchar(255) not null, primary key (id)) engine=InnoDB;
create table artist (bgg_id bigint, id bigint not null auto_increment, name varchar(255) not null, primary key (id)) engine=InnoDB;
create table board_game (max_players integer not null, min_players integer not null, playing_time integer not null, year_published integer not null, bgg_id bigint, id bigint not null auto_increment, description varchar(10000), image_link varchar(255), name varchar(255) not null, primary key (id)) engine=InnoDB;
create table board_game_artist (artists_id bigint not null, board_games_id bigint not null) engine=InnoDB;
create table board_game_category (board_games_id bigint not null, categories_id bigint not null) engine=InnoDB;
