create table category (bgg_id bigint, id bigint not null auto_increment, name varchar(255) not null, primary key (id)) engine=InnoDB;
create table expansion (max_players integer not null, min_players integer not null, playing_time integer not null, year_published integer not null, bgg_id bigint, board_game_id bigint, id bigint not null auto_increment, description varchar(10000), image_link varchar(255), name varchar(255) not null, primary key (id)) engine=InnoDB;
create table mechanic (bgg_id bigint, id bigint not null auto_increment, name varchar(255) not null, primary key (id)) engine=InnoDB;
create table role (description varchar(255), pid varchar(255) not null, primary key (pid)) engine=InnoDB;
create table user_table (pid bigint not null auto_increment, first_name varchar(100) not null, last_name varchar(100) not null, login varchar(100) not null, middle_name varchar(100), password varchar(255) not null, status varchar(20) default 'ACTIVE' not null, primary key (pid)) engine=InnoDB;
create table users_roles (users_pid bigint not null, roles_pid varchar(255) not null) engine=InnoDB;
